using Microsoft.VisualStudio.TestTools.UnitTesting;
using System.Linq;
using CJG.Testing.Core;
using CJG.Core.Entities;
using CJG.Application.Services;

namespace CJG.Testing.UnitTests.Entities
{
	[TestClass()]
	public class ProgramConfigurationValidationTests
	{
		ServiceHelper helper { get; set; }

		[TestInitialize]
		public void Setup()
		{
			// Arrange
			var user = EntityHelper.CreateExternalUser();

			helper = new ServiceHelper(typeof(ProgramConfigurationService), user);
			helper.MockContext();
			helper.MockDbSet<ProgramConfiguration>();
		}

		[TestMethod, TestCategory("Program Configuration"), TestCategory("Validate")]
		public void Validate_When_Program_Configuration_Caption_Is_Missing()
		{
			ProgramConfiguration programConfiguration = new ProgramConfiguration();
			programConfiguration.UserGuidanceClaims = "";
			programConfiguration.UserGuidanceCostEstimates = "";

			var service = helper.Create<ProgramConfigurationService>();
			// Act
			var validationResults = service.Validate(programConfiguration).ToArray();

			// Assert
			Assert.AreEqual(true, validationResults.Any(x => x.ErrorMessage == "The caption is required."));
			Assert.AreEqual(true, validationResults.Any(x => x.ErrorMessage == "The field UserGuidanceClaims must be a string or array type with a maximum length of '1000'."));
			Assert.AreEqual(true, validationResults.Any(x => x.ErrorMessage == "The field UserGuidanceCostEstimates must be a string or array type with a maximum length of '1000'."));
		}

	}
}